    private void defaultLookandfeelItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_defaultLookandfeelItemStateChanged
        try{
        if(defaultLookandfeel.getSelectedItem().toString().equalsIgnoreCase("synth"))
            UIManager.setLookAndFeel("javax.swing.plaf.synth.SynthLookAndFeel");
        else if(defaultLookandfeel.getSelectedItem().toString().equalsIgnoreCase("multi"))
            UIManager.setLookAndFeel("javax.swing.plaf.multi.MultiLookAndFeel");
        else if(defaultLookandfeel.getSelectedItem().toString().equalsIgnoreCase("metal"))
            UIManager.setLookAndFeel("javax.swing.plaf.metal.MetalTheme");
        else if(defaultLookandfeel.getSelectedItem().toString().equalsIgnoreCase("nimbus"))
            UIManager.setLookAndFeel("javax.swing.plaf.nimbus.NimbusLookAndFeel");
        }
        catch(Exception ae){
        DialogBox.dialog.errorDialog(ae.getMessage());
        }
    }//GEN-LAST:event_defaultLookandfeelItemStateChanged

