    void setValues(){
    ReadConfigFile.conf.rootUsername = this.rootUsername.getText();
    ReadConfigFile.conf.rootPassword = this.rootPassword.getText();
    ReadConfigFile.conf.defaultWebcam = this.defaultWebcam.getSelectedItem().toString();
    ReadConfigFile.conf.defaultFingerprint = this.defaultFingerPrintReader.getSelectedItem().toString();
    ReadConfigFile.conf.defaultLookAndFeel = this.defaultLookandfeel.getSelectedItem().toString();
    }
    void getPrevious(){
    this.rootUsername.setText(ReadConfigFile.conf.rootUsername);
    this.rootPassword.setText(ReadConfigFile.conf.rootPassword);
    this.defaultWebcam.setSelectedItem(ReadConfigFile.conf.defaultWebcam);
    this.defaultFingerPrintReader.setSelectedItem(ReadConfigFile.conf.defaultFingerprint);
    this.defaultLookandfeel.setSelectedItem(ReadConfigFile.conf.defaultLookAndFeel);
    }
